@.+2a void ex_script(FILE *fp)
{
	char done = 0;
	do {
		size_t n = 128, i = 0;
		int c;
		char *ln = malloc(128);
		while ((c = fgetc(fp)) != EOF && c != '\\\\n') {
			if (i >= n - 2) {
				n += 128;
				ln = erealloc(ln, n);
			}
			ln[i++] = c;
		}
		if (!i) {
			free(ln);
			done = 1;
			break;
		}
		ln[i] = '\\\\0';
		ex_command(ln);
		free(ln);
	} while(!done);
}

void load_exrc(char *exrc)
{
	struct stat st;
	if (stat(exrc, &st) == 0) {
		if (st.st_uid == getuid() && !(st.st_mode & S_IWGRP) && !(st.st_mode & S_IWOTH)) {
			FILE *fp = fopen(exrc, \"r\");
			if (fp) {
				ex_script(fp);
				fclose(fp);
			} else {
				fprintf(stderr, \"Cannot open %s\\\\n\", exrc);
				exit(EXIT_FAILURE);
			}
		} else {
			fprintf(stderr, \"Bad permissions on %s\\\\n\", exrc);
			exit(EXIT_FAILURE);
		}
	}
}

