#!/bin/sh
# Generated by patch2vi from unified diff
# Separator: '@'
set -e

# Path to nextvi (adjust as needed)
VI=${VI:-vi}

# Verify that VI is nextvi
if ! $VI -? 2>&1 | grep -q 'Nextvi'; then
    echo "Error: $VI is not nextvi" >&2
    echo "Set VI environment variable to point to nextvi" >&2
    exit 1
fi

# Patch: ex.c
EXINIT="rcm:|sc! @|vis 6@1397a 	{\"ro\", ec_readonly},
.
@1316a static void *ec_readonly(char *loc, char *cmd, char *arg)
{
	ex_buf->readonly = !ex_buf->readonly;
	return NULL;
}

.
@679a 			if (ex_buf->readonly)
				return \"write failed: readonly option is set\";
.
@366a 	if (access(arg, F_OK) == 0 && access(arg, W_OK) == -1)
		ex_buf->readonly = 1;
.
@115a 	bufs[i].readonly = readonly;
.
@54a char readonly = 0;		/* commandline readonly option */
.
@vis 4@wq" $VI -e 'ex.c'

# Patch: vi.c
EXINIT="rcm:|sc! @|vis 6@1816;46c R
.
@1813a 			else if (argv[i][j] == 'R')
				readonly = 1;
.
@vis 4@wq" $VI -e 'vi.c'

# Patch: vi.h
EXINIT="rcm:|sc! @|vis 6@543a extern char readonly;
.
@410a 	char readonly;			/* read only */
.
@vis 4@wq" $VI -e 'vi.h'
