static void *ec_equalize(char *loc, char *cmd, char *arg)
{
	struct win *w, **group;
	int ngroup = 0, i, j;
	if (nwins <= 1)
		return NULL;
	group = emalloc(nwins * sizeof(struct win *));
	/* try horizontal grouping: same y and h as curwin */
	w = wins;
	do {
		if (w->y == curwin->y && w->h == curwin->h)
			group[ngroup++] = w;
		w = w->next;
	} while (w != wins);
	if (ngroup > 1) {
		/* sort by x */
		for (i = 0; i < ngroup - 1; i++)
			for (j = i + 1; j < ngroup; j++)
				if (group[j]->x < group[i]->x) {
					struct win *tmp = group[i];
					group[i] = group[j];
					group[j] = tmp;
				}
		/* check contiguity */
		int contiguous = 1;
		for (i = 0; i < ngroup - 1; i++) {
			if (group[i]->x + group[i]->w + 1 != group[i+1]->x) {
				contiguous = 0;
				break;
			}
		}
		if (contiguous) {
			/* redistribute width evenly */
			int total = group[ngroup-1]->x + group[ngroup-1]->w - group[0]->x;
			int new_w = (total - (ngroup - 1)) / ngroup;
			int extra = (total - (ngroup - 1)) % ngroup;
			int x = group[0]->x;
			for (i = 0; i < ngroup; i++) {
				group[i]->x = x;
				group[i]->w = new_w + (i < extra ? 1 : 0);
				x += group[i]->w + 1;
			}
			free(group);
			return NULL;
		}
	}
	/* try vertical grouping: same x and w as curwin */
	ngroup = 0;
	w = wins;
	do {
		if (w->x == curwin->x && w->w == curwin->w)
			group[ngroup++] = w;
		w = w->next;
	} while (w != wins);
	if (ngroup > 1) {
		/* sort by y */
		for (i = 0; i < ngroup - 1; i++)
			for (j = i + 1; j < ngroup; j++)
				if (group[j]->y < group[i]->y) {
					struct win *tmp = group[i];
					group[i] = group[j];
					group[j] = tmp;
				}
		/* check contiguity */
		int contiguous = 1;
		for (i = 0; i < ngroup - 1; i++) {
			if (group[i]->y + group[i]->h + 1 != group[i+1]->y) {
				contiguous = 0;
				break;
			}
		}
		if (contiguous) {
			/* redistribute height evenly */
			int is_bottom = (group[ngroup-1]->y + group[ngroup-1]->h == xrows);
			int total = group[ngroup-1]->y + group[ngroup-1]->h
				+ (is_bottom ? vi_status : 0) - group[0]->y;
			int new_h = (total - (ngroup - 1)) / ngroup;
			int extra = (total - (ngroup - 1)) % ngroup;
			int y = group[0]->y;
			for (i = 0; i < ngroup; i++) {
				group[i]->y = y;
				group[i]->h = new_h + (extra > 0 ? 1 : 0);
				if (extra > 0) extra--;
				/* bottom window absorbs vi_status cost */
				if (i == ngroup - 1 && is_bottom)
					group[i]->h -= vi_status;
				y += group[i]->h + 1;
			}
			free(group);
			return NULL;
		}
	}
	free(group);
	return NULL;
}

