+		newwin->row = 0;
+		newwin->off = 0;
+		newwin->top = 0;
+		newwin->left = 0;
+	} else {
+		/* same buffer as current window */
+		newwin->buf = curwin->buf;
+	}
+
+	/* insert into circular list after current window */
+	newwin->next = curwin->next;
+	curwin->next = newwin;
+	nwins++;
+
+	return NULL;
+}
