${SEP}.+2a /* draw all window separators */
static void vi_draw_separators(void)
{
	struct win *w = wins, *w2;
	do {
		w2 = wins;
		do {
			if (w2 != w) {
				if (w2->x == w->x + w->w + 1) {
					int y1 = MAX(w->y, w2->y);
					int y2 = MIN(w->y + w->h, w2->y + w2->h);
					if (y1 < y2)
						vi_draw_vsep(w->x + w->w, y1, y2 - y1);
				}
				if (w2->y == w->y + w->h + 1) {
					int x1 = MAX(w->x, w2->x);
					int x2 = MIN(w->x + w->w, w2->x + w2->w);
					if (x1 < x2)
						vi_draw_hsep(w->y + w->h, x1, x2 - x1);
				}
			}
			w2 = w2->next;
		} while (w2 != wins);
		w = w->next;
	} while (w != wins);
}

/* draw all windows and separators */
static void vi_draw_allwins(void)
{
	struct win *w = wins;
	struct win *saved = curwin;
	struct buf *savebuf = ex_buf;
	int srow = xrow, soff = xoff, stop = xtop, sleft = xleft;
	do {
		curwin = w;
		xrow = w->row;
		xoff = w->off;
		xtop = w->top;
		xleft = w->left;
		if (w->buf != ex_buf) {
			ex_buf = w->buf;
			syn_setft(xb_ft);
		}
		syn_scdir(0);
		for (int i = xtop; i < xtop + w->h; i++)
			vi_drawrow(i);
		w = w->next;
	} while (w != wins);
	vi_draw_separators();
	curwin = saved;
	xrow = srow;
	xoff = soff;
	xtop = stop;
	xleft = sleft;
	if (savebuf != ex_buf) {
		ex_buf = savebuf;
		syn_setft(xb_ft);
	}
}

