	la.s   = s;
	ren_state *r = ren_position(s);
	int o_beg, o_end;
	if (vi_visual == 'V') {
		o_beg = 0;
		o_end = r->n - 1;
	} else if (vi_visual == 'b') {
		o_beg = MIN(vi_voff, xoff);
		o_end = MAX(vi_voff, xoff);
